var sys = require('sys');
var fs = require('fs');

var db = require('../lib/redis').createStreamingClient(6379, "127.0.0.1");

// print last stdin

sys.pump(db.createReadStream("get", "stdin"), process.stdout);

// delete last stdin

db.query("del", "stdin");

// stream stdin to database

sys.pump(process.openStdin(), db.createWriteStream("append", "stdin"));

